CREATE TABLE convex_hulls (
    "id" INTEGER PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
    "name" TEXT,
    "created" TIMESTAMP
);
CREATE TABLE points(
    "id" INTEGER PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
    "input" JSON NOT NULL,
    "output" JSON NOT NULL,
    "convex_hull_id" INTEGER REFERENCES convex_hulls ON DELETE CASCADE
);